<script>
  import Api from '../../api/mascot'
  import Bus from '../../bus'

  export default {
    props: ['id'],
    created() {
      this.listen()
      this.fetch()
    },
    data() {
      return {
        data: null
      }
    },

    methods: {
      listen() {
        Echo.channel(`mascot_${this.id}`)
          .listen('Mascots.LevelChanged', response => {
            this.data = Object.assign({}, response.mascot)
          })
      },
      fetch() {
        Api.find(this.id)
          .then(response => {
            this.data = Object.assign({}, response.data.data)
          })
      },
      level(level) {
        if (level <= 0) return 0

        if (level === 1) return 25

        if (level === 2) return 30

        if (level === 3) return 35

        if (level === 4) return 40

        if (level === 5) return 45
      },
      percentage() {
        let workload = this.level(this.data.level) - this.level(this.data.level - 1)
        let progress = this.level(this.data.level) - this.data.exp
        return parseFloat(((workload - progress) / workload * 100).toFixed(1))
      }
    },
  }
</script>